import * as Ark from '@ark-ui/react/tabs'
import { styled } from 'styled-system/jsx'
import { tabs, type TabsVariantProps } from 'styled-system/recipes'
import { createStyleContext } from '~/lib/create-style-context'

const { withProvider, withContext } = createStyleContext(tabs)

export * from '@ark-ui/react/tabs'
export type TabsProps = Ark.TabsProps & TabsVariantProps

const TabsRoot = withProvider(styled(Ark.Tabs.Root), 'root')
export const TabsContent = withContext(styled(Ark.Tabs.Content), 'content')
export const TabsIndicator = withContext(styled(Ark.Tabs.Indicator), 'indicator')
export const TabsList = withContext(styled(Ark.Tabs.List), 'list')
export const TabsTrigger = withContext(styled(Ark.Tabs.Trigger), 'trigger')

export const Tabs = Object.assign(TabsRoot, {
  Root: TabsRoot,
  Content: TabsContent,
  Indicator: TabsIndicator,
  List: TabsList,
  Trigger: TabsTrigger,
})
