import { Checkbox as ArkCheckbox, type CheckboxProps as ArkCheckboxProps } from '@ark-ui/solid'
import { styled } from 'styled-system/jsx'
import { checkbox, type CheckboxVariantProps } from 'styled-system/recipes'
import { createStyleContext } from '~/lib/create-style-context'

const { withProvider, withContext } = createStyleContext(checkbox)

export type CheckboxProps = ArkCheckboxProps & CheckboxVariantProps

const CheckboxRoot = withProvider(styled(ArkCheckbox.Root), 'root')
export const CheckboxControl = withContext(styled(ArkCheckbox.Control), 'control')
export const CheckboxIndicator = withContext(styled(ArkCheckbox.Indicator), 'indicator')
export const CheckboxLabel = withContext(styled(ArkCheckbox.Label), 'label')

export const Checkbox = Object.assign(CheckboxRoot, {
  Root: CheckboxRoot,
  Control: CheckboxControl,
  Indicator: CheckboxIndicator,
  Label: CheckboxLabel,
})
