import { Tabs as ArkTabs, type TabsProps as ArkTabsProps } from '@ark-ui/solid'
import { styled } from 'styled-system/jsx'
import { tabs, type TabsVariantProps } from 'styled-system/recipes'
import { createStyleContext } from '~/lib/create-style-context'

const { withProvider, withContext } = createStyleContext(tabs)

export type TabsProps = ArkTabsProps & TabsVariantProps

const TabsRoot = withProvider(styled(ArkTabs.Root), 'root')
export const TabsContent = withContext(styled(ArkTabs.Content), 'content')
export const TabsIndicator = withContext(styled(ArkTabs.Indicator), 'indicator')
export const TabsList = withContext(styled(ArkTabs.List), 'list')
export const TabsTrigger = withContext(styled(ArkTabs.Trigger), 'trigger')

export const Tabs = Object.assign(TabsRoot, {
  Root: TabsRoot,
  Content: TabsContent,
  Indicator: TabsIndicator,
  List: TabsList,
  Trigger: TabsTrigger,
})
