import { type Assign, Avatar } from '@ark-ui/solid'
import type { ComponentProps } from 'solid-js'
import { avatar } from 'styled-system/recipes'
import type { HTMLStyledProps } from 'styled-system/types'
import { createStyleContext } from '~/lib/create-style-context'

const { withProvider, withContext } = createStyleContext(avatar)

export type RootProviderProps = ComponentProps<typeof RootProvider>
export const RootProvider = withProvider<Assign<HTMLStyledProps<'div'>, Avatar.RootProviderProps>>(
  Avatar.RootProvider,
  'root',
)

export type RootProps = ComponentProps<typeof Root>
export const Root = withProvider<Assign<HTMLStyledProps<'div'>, Avatar.RootProps>>(
  Avatar.Root,
  'root',
)

export type FallbackProps = ComponentProps<typeof Fallback>
export const Fallback = withContext<Assign<HTMLStyledProps<'span'>, Avatar.FallbackProps>>(
  Avatar.Fallback,
  'fallback',
)

export type ImageProps = ComponentProps<typeof Image>
export const Image = withContext<Assign<HTMLStyledProps<'img'>, Avatar.ImageProps>>(
  Avatar.Image,
  'image',
)

export {
  AvatarContext as Context,
  type AvatarContextProps as ContextProps,
} from '@ark-ui/solid'
