import { type Assign, HoverCard } from '@ark-ui/solid'
import type { ComponentProps } from 'solid-js'
import { hoverCard } from 'styled-system/recipes'
import type { HTMLStyledProps } from 'styled-system/types'
import { createStyleContext } from '~/lib/create-style-context'

const { withRootProvider, withContext } = createStyleContext(hoverCard)

export type RootProviderProps = ComponentProps<typeof RootProvider>
export const RootProvider = withRootProvider<HoverCard.RootProviderProps>(HoverCard.RootProvider)

export type RootProps = ComponentProps<typeof Root>
export const Root = withRootProvider<HoverCard.RootProps>(HoverCard.Root)

export type ArrowProps = ComponentProps<typeof Arrow>
export const Arrow = withContext<Assign<HTMLStyledProps<'div'>, HoverCard.ArrowProps>>(
  HoverCard.Arrow,
  'arrow',
)

export type ArrowTipProps = ComponentProps<typeof ArrowTip>
export const ArrowTip = withContext<Assign<HTMLStyledProps<'div'>, HoverCard.ArrowTipProps>>(
  HoverCard.ArrowTip,
  'arrowTip',
)

export type ContentProps = ComponentProps<typeof Content>
export const Content = withContext<Assign<HTMLStyledProps<'div'>, HoverCard.ContentProps>>(
  HoverCard.Content,
  'content',
)

export type PositionerProps = ComponentProps<typeof Positioner>
export const Positioner = withContext<Assign<HTMLStyledProps<'div'>, HoverCard.PositionerProps>>(
  HoverCard.Positioner,
  'positioner',
)

export type TriggerProps = ComponentProps<typeof Trigger>
export const Trigger = withContext<Assign<HTMLStyledProps<'button'>, HoverCard.TriggerProps>>(
  HoverCard.Trigger,
  'trigger',
)

export {
  HoverCardContext as Context,
  type HoverCardContextProps as ContextProps,
} from '@ark-ui/solid'
