import { type Assign, Slider } from '@ark-ui/solid'
import type { ComponentProps } from 'solid-js'
import { slider } from 'styled-system/recipes'
import type { HTMLStyledProps } from 'styled-system/types'
import { createStyleContext } from '~/lib/create-style-context'

const { withProvider, withContext } = createStyleContext(slider)

export type RootProviderProps = ComponentProps<typeof RootProvider>
export const RootProvider = withProvider<Assign<HTMLStyledProps<'div'>, Slider.RootProviderProps>>(
  Slider.RootProvider,
  'root',
)

export type RootProps = ComponentProps<typeof Root>
export const Root = withProvider<Assign<HTMLStyledProps<'div'>, Slider.RootProps>>(
  Slider.Root,
  'root',
)

export type ControlProps = ComponentProps<typeof Control>
export const Control = withContext<Assign<HTMLStyledProps<'div'>, Slider.ControlProps>>(
  Slider.Control,
  'control',
)

export type LabelProps = ComponentProps<typeof Label>
export const Label = withContext<Assign<HTMLStyledProps<'label'>, Slider.LabelProps>>(
  Slider.Label,
  'label',
)

export type MarkerGroupProps = ComponentProps<typeof MarkerGroup>
export const MarkerGroup = withContext<Assign<HTMLStyledProps<'div'>, Slider.MarkerGroupProps>>(
  Slider.MarkerGroup,
  'markerGroup',
)

export type MarkerProps = ComponentProps<typeof Marker>
export const Marker = withContext<Assign<HTMLStyledProps<'span'>, Slider.MarkerProps>>(
  Slider.Marker,
  'marker',
)

export type RangeProps = ComponentProps<typeof Range>
export const Range = withContext<Assign<HTMLStyledProps<'div'>, Slider.RangeProps>>(
  Slider.Range,
  'range',
)

export type ThumbProps = ComponentProps<typeof Thumb>
export const Thumb = withContext<Assign<HTMLStyledProps<'div'>, Slider.ThumbProps>>(
  Slider.Thumb,
  'thumb',
)

export type TrackProps = ComponentProps<typeof Track>
export const Track = withContext<Assign<HTMLStyledProps<'div'>, Slider.TrackProps>>(
  Slider.Track,
  'track',
)

export type ValueTextProps = ComponentProps<typeof ValueText>
export const ValueText = withContext<Assign<HTMLStyledProps<'span'>, Slider.ValueTextProps>>(
  Slider.ValueText,
  'valueText',
)

export {
  SliderContext as Context,
  type SliderContextProps as ContextProps,
  SliderHiddenInput as HiddenInput,
  type SliderHiddenInputProps as HiddenInputProps,
} from '@ark-ui/solid'
