import { type Assign, Tooltip } from '@ark-ui/solid'
import type { ComponentProps } from 'solid-js'
import { tooltip } from 'styled-system/recipes'
import type { HTMLStyledProps } from 'styled-system/types'
import { createStyleContext } from '~/lib/create-style-context'

const { withRootProvider, withContext } = createStyleContext(tooltip)

export type RootProviderProps = ComponentProps<typeof RootProvider>
export const RootProvider = withRootProvider<Tooltip.RootProviderProps>(Tooltip.RootProvider)

export type RootProps = ComponentProps<typeof Root>
export const Root = withRootProvider<Tooltip.RootProps>(Tooltip.Root)

export type ArrowProps = ComponentProps<typeof Arrow>
export const Arrow = withContext<Assign<HTMLStyledProps<'div'>, Tooltip.ArrowProps>>(
  Tooltip.Arrow,
  'arrow',
)

export type ArrowTipProps = ComponentProps<typeof ArrowTip>
export const ArrowTip = withContext<Assign<HTMLStyledProps<'div'>, Tooltip.ArrowTipProps>>(
  Tooltip.ArrowTip,
  'arrowTip',
)

export type ContentProps = ComponentProps<typeof Content>
export const Content = withContext<Assign<HTMLStyledProps<'div'>, Tooltip.ContentProps>>(
  Tooltip.Content,
  'content',
)

export type PositionerProps = ComponentProps<typeof Positioner>
export const Positioner = withContext<Assign<HTMLStyledProps<'div'>, Tooltip.PositionerProps>>(
  Tooltip.Positioner,
  'positioner',
)

export type TriggerProps = ComponentProps<typeof Trigger>
export const Trigger = withContext<Assign<HTMLStyledProps<'button'>, Tooltip.TriggerProps>>(
  Tooltip.Trigger,
  'trigger',
)

export {
  TooltipContext as Context,
  type TooltipContextProps as ContextProps,
} from '@ark-ui/solid'
