import type { Assign } from '@ark-ui/vue'
import { Avatar } from '@ark-ui/vue/avatar'
import { avatar } from 'styled-system/recipes'
import type { HTMLStyledProps } from 'styled-system/types'
import type { Component } from 'vue'
import { createStyleContext } from '~/lib/create-style-context'

const { withProvider, withContext } = createStyleContext(avatar)

export type RootProviderProps = Component<typeof RootProvider>
export const RootProvider = withProvider<
  Assign<HTMLStyledProps<'div'>, Avatar.RootProviderBaseProps>
>(Avatar.RootProvider, 'root')

export type RootProps = Component<typeof Root>
export const Root = withProvider<Assign<HTMLStyledProps<'div'>, Avatar.RootBaseProps>>(
  Avatar.Root,
  'root',
)

export type FallbackProps = Component<typeof Fallback>
export const Fallback = withContext<Assign<HTMLStyledProps<'span'>, Avatar.FallbackBaseProps>>(
  Avatar.Fallback,
  'fallback',
)

export type ImageProps = Component<typeof Image>
export const Image = withContext<Assign<HTMLStyledProps<'img'>, Avatar.ImageBaseProps>>(
  Avatar.Image,
  'image',
)

export {
  AvatarContext as Context,
  type AvatarContextProps as ContextProps,
} from '@ark-ui/vue/avatar'
