import type { Assign } from '@ark-ui/vue'
import { Clipboard } from '@ark-ui/vue/clipboard'
import { clipboard } from 'styled-system/recipes'
import type { HTMLStyledProps } from 'styled-system/types'
import type { Component } from 'vue'
import { createStyleContext } from '~/lib/create-style-context'

const { withProvider, withContext } = createStyleContext(clipboard)

export type RootProviderProps = Component<typeof RootProvider>
export const RootProvider = withProvider<
  Assign<HTMLStyledProps<'div'>, Clipboard.RootProviderBaseProps>
>(Clipboard.RootProvider, 'root')

export type RootProps = Component<typeof Root>
export const Root = withProvider<Assign<HTMLStyledProps<'div'>, Clipboard.RootBaseProps>>(
  Clipboard.Root,
  'root',
)

export type ControlProps = Component<typeof Control>
export const Control = withContext<Assign<HTMLStyledProps<'div'>, Clipboard.ControlBaseProps>>(
  Clipboard.Control,
  'control',
)

export type IndicatorProps = Component<typeof Indicator>
export const Indicator = withContext<Assign<HTMLStyledProps<'div'>, Clipboard.IndicatorBaseProps>>(
  Clipboard.Indicator,
  'indicator',
)

export type InputProps = Component<typeof Input>
export const Input = withContext<Assign<HTMLStyledProps<'input'>, Clipboard.InputBaseProps>>(
  Clipboard.Input,
  'input',
)

export type LabelProps = Component<typeof Label>
export const Label = withContext<Assign<HTMLStyledProps<'label'>, Clipboard.LabelBaseProps>>(
  Clipboard.Label,
  'label',
)

export type TriggerProps = Component<typeof Trigger>
export const Trigger = withContext<Assign<HTMLStyledProps<'button'>, Clipboard.TriggerBaseProps>>(
  Clipboard.Trigger,
  'trigger',
)

export {
  ClipboardContext as Context,
  type ClipboardContextProps as ContextProps,
} from '@ark-ui/vue/clipboard'
