import type { Assign } from '@ark-ui/vue'
import { Collapsible } from '@ark-ui/vue/collapsible'
import { collapsible } from 'styled-system/recipes'
import type { HTMLStyledProps } from 'styled-system/types'
import type { Component } from 'vue'
import { createStyleContext } from '~/lib/create-style-context'

const { withProvider, withContext } = createStyleContext(collapsible)

export type RootProviderProps = Component<typeof RootProvider>
export const RootProvider = withProvider<
  Assign<HTMLStyledProps<'div'>, Collapsible.RootProviderBaseProps>
>(Collapsible.RootProvider, 'root')

export type RootProps = Component<typeof Root>
export const Root = withProvider<Assign<HTMLStyledProps<'div'>, Collapsible.RootBaseProps>>(
  Collapsible.Root,
  'root',
)

export type ContentProps = Component<typeof Content>
export const Content = withContext<Assign<HTMLStyledProps<'div'>, Collapsible.ContentBaseProps>>(
  Collapsible.Content,
  'content',
)

export type TriggerProps = Component<typeof Trigger>
export const Trigger = withContext<Assign<HTMLStyledProps<'button'>, Collapsible.TriggerBaseProps>>(
  Collapsible.Trigger,
  'trigger',
)

export {
  CollapsibleContext as Context,
  type CollapsibleContextProps as ContextProps,
} from '@ark-ui/vue/collapsible'
