import type { Assign } from '@ark-ui/vue'
import { Dialog } from '@ark-ui/vue/dialog'
import { dialog } from 'styled-system/recipes'
import type { HTMLStyledProps } from 'styled-system/types'
import type { Component } from 'vue'
import { createStyleContext } from '~/lib/create-style-context'

const { withRootProvider, withContext } = createStyleContext(dialog)

export type RootProviderProps = Component<typeof RootProvider>
export const RootProvider = withRootProvider<Dialog.RootProviderBaseProps>(Dialog.RootProvider)

export type RootProps = Component<typeof Root>
export const Root = withRootProvider<Dialog.RootBaseProps>(Dialog.Root)

export type BackdropProps = Component<typeof Backdrop>
export const Backdrop = withContext<Assign<HTMLStyledProps<'div'>, Dialog.BackdropBaseProps>>(
  Dialog.Backdrop,
  'backdrop',
)

export type CloseTriggerProps = Component<typeof CloseTrigger>
export const CloseTrigger = withContext<
  Assign<HTMLStyledProps<'button'>, Dialog.CloseTriggerBaseProps>
>(Dialog.CloseTrigger, 'closeTrigger')

export type ContentProps = Component<typeof Content>
export const Content = withContext<Assign<HTMLStyledProps<'div'>, Dialog.ContentBaseProps>>(
  Dialog.Content,
  'content',
)

export type DescriptionProps = Component<typeof Description>
export const Description = withContext<Assign<HTMLStyledProps<'div'>, Dialog.DescriptionBaseProps>>(
  Dialog.Description,
  'description',
)

export type PositionerProps = Component<typeof Positioner>
export const Positioner = withContext<Assign<HTMLStyledProps<'div'>, Dialog.PositionerBaseProps>>(
  Dialog.Positioner,
  'positioner',
)

export type TitleProps = Component<typeof Title>
export const Title = withContext<Assign<HTMLStyledProps<'h2'>, Dialog.TitleBaseProps>>(
  Dialog.Title,
  'title',
)

export type TriggerProps = Component<typeof Trigger>
export const Trigger = withContext<Assign<HTMLStyledProps<'button'>, Dialog.TriggerBaseProps>>(
  Dialog.Trigger,
  'trigger',
)

export {
  DialogContext as Context,
  type DialogContextProps as ContextProps,
} from '@ark-ui/vue/dialog'
