import type { Assign } from '@ark-ui/vue'
import { HoverCard } from '@ark-ui/vue/hover-card'
import { hoverCard } from 'styled-system/recipes'
import type { HTMLStyledProps } from 'styled-system/types'
import type { Component } from 'vue'
import { createStyleContext } from '~/lib/create-style-context'

const { withRootProvider, withContext } = createStyleContext(hoverCard)

export type RootProviderProps = Component<typeof RootProvider>
export const RootProvider = withRootProvider<HoverCard.RootProviderBaseProps>(
  HoverCard.RootProvider,
)

export type RootProps = Component<typeof Root>
export const Root = withRootProvider<HoverCard.RootBaseProps>(HoverCard.Root)

export type ArrowProps = Component<typeof Arrow>
export const Arrow = withContext<Assign<HTMLStyledProps<'div'>, HoverCard.ArrowBaseProps>>(
  HoverCard.Arrow,
  'arrow',
)

export type ArrowTipProps = Component<typeof ArrowTip>
export const ArrowTip = withContext<Assign<HTMLStyledProps<'div'>, HoverCard.ArrowTipBaseProps>>(
  HoverCard.ArrowTip,
  'arrowTip',
)

export type ContentProps = Component<typeof Content>
export const Content = withContext<Assign<HTMLStyledProps<'div'>, HoverCard.ContentBaseProps>>(
  HoverCard.Content,
  'content',
)

export type PositionerProps = Component<typeof Positioner>
export const Positioner = withContext<
  Assign<HTMLStyledProps<'div'>, HoverCard.PositionerBaseProps>
>(HoverCard.Positioner, 'positioner')

export type TriggerProps = Component<typeof Trigger>
export const Trigger = withContext<Assign<HTMLStyledProps<'button'>, HoverCard.TriggerBaseProps>>(
  HoverCard.Trigger,
  'trigger',
)

export {
  HoverCardContext as Context,
  type HoverCardContextProps as ContextProps,
} from '@ark-ui/vue/hover-card'
