import type { Assign } from '@ark-ui/vue'
import { Menu } from '@ark-ui/vue/menu'
import { menu } from 'styled-system/recipes'
import type { HTMLStyledProps } from 'styled-system/types'
import type { Component } from 'vue'
import { createStyleContext } from '~/lib/create-style-context'

const { withRootProvider, withContext } = createStyleContext(menu)

export type RootProviderProps = Component<typeof RootProvider>
export const RootProvider = withRootProvider<Menu.RootProviderBaseProps>(Menu.RootProvider)

export type RootProps = Component<typeof Root>
export const Root = withRootProvider<Menu.RootBaseProps>(Menu.Root)

export type ArrowProps = Component<typeof Arrow>
export const Arrow = withContext<Assign<HTMLStyledProps<'div'>, Menu.ArrowBaseProps>>(
  Menu.Arrow,
  'arrow',
)

export type ArrowTipProps = Component<typeof ArrowTip>
export const ArrowTip = withContext<Assign<HTMLStyledProps<'div'>, Menu.ArrowTipBaseProps>>(
  Menu.ArrowTip,
  'arrowTip',
)

export type CheckboxItemProps = Component<typeof CheckboxItem>
export const CheckboxItem = withContext<Assign<HTMLStyledProps<'div'>, Menu.CheckboxItemBaseProps>>(
  Menu.CheckboxItem,
  'checkboxItem',
)

export type ContentProps = Component<typeof Content>
export const Content = withContext<Assign<HTMLStyledProps<'div'>, Menu.ContentBaseProps>>(
  Menu.Content,
  'content',
)

export type ContextTriggerProps = Component<typeof ContextTrigger>
export const ContextTrigger = withContext<
  Assign<HTMLStyledProps<'button'>, Menu.ContextTriggerBaseProps>
>(Menu.ContextTrigger, 'contextTrigger')

export type IndicatorProps = Component<typeof Indicator>
export const Indicator = withContext<Assign<HTMLStyledProps<'div'>, Menu.IndicatorBaseProps>>(
  Menu.Indicator,
  'indicator',
)

export type ItemGroupLabelProps = Component<typeof ItemGroupLabel>
export const ItemGroupLabel = withContext<
  Assign<HTMLStyledProps<'div'>, Menu.ItemGroupLabelBaseProps>
>(Menu.ItemGroupLabel, 'itemGroupLabel')

export type ItemGroupProps = Component<typeof ItemGroup>
export const ItemGroup = withContext<Assign<HTMLStyledProps<'div'>, Menu.ItemGroupBaseProps>>(
  Menu.ItemGroup,
  'itemGroup',
)

export type ItemIndicatorProps = Component<typeof ItemIndicator>
export const ItemIndicator = withContext<
  Assign<HTMLStyledProps<'div'>, Menu.ItemIndicatorBaseProps>
>(Menu.ItemIndicator, 'itemIndicator')

export type ItemProps = Component<typeof Item>
export const Item = withContext<Assign<HTMLStyledProps<'div'>, Menu.ItemBaseProps>>(
  Menu.Item,
  'item',
)

export type ItemTextProps = Component<typeof ItemText>
export const ItemText = withContext<Assign<HTMLStyledProps<'div'>, Menu.ItemTextBaseProps>>(
  Menu.ItemText,
  'itemText',
)

export type PositionerProps = Component<typeof Positioner>
export const Positioner = withContext<Assign<HTMLStyledProps<'div'>, Menu.PositionerBaseProps>>(
  Menu.Positioner,
  'positioner',
)

export type RadioItemGroupProps = Component<typeof RadioItemGroup>
export const RadioItemGroup = withContext<
  Assign<HTMLStyledProps<'div'>, Menu.RadioItemGroupBaseProps>
>(Menu.RadioItemGroup, 'radioItemGroup')

export type RadioItemProps = Component<typeof RadioItem>
export const RadioItem = withContext<Assign<HTMLStyledProps<'div'>, Menu.RadioItemBaseProps>>(
  Menu.RadioItem,
  'radioItem',
)

export type SeparatorProps = Component<typeof Separator>
export const Separator = withContext<Assign<HTMLStyledProps<'hr'>, Menu.SeparatorBaseProps>>(
  Menu.Separator,
  'separator',
)

export type TriggerItemProps = Component<typeof TriggerItem>
export const TriggerItem = withContext<Assign<HTMLStyledProps<'div'>, Menu.TriggerItemBaseProps>>(
  Menu.TriggerItem,
  'triggerItem',
)

export type TriggerProps = Component<typeof Trigger>
export const Trigger = withContext<Assign<HTMLStyledProps<'button'>, Menu.TriggerBaseProps>>(
  Menu.Trigger,
  'trigger',
)

export {
  MenuContext as Context,
  type MenuContextProps as ContextProps,
} from '@ark-ui/vue/menu'
