import type { Assign } from '@ark-ui/vue'
import { Slider } from '@ark-ui/vue/slider'
import { slider } from 'styled-system/recipes'
import type { HTMLStyledProps } from 'styled-system/types'
import type { Component } from 'vue'
import { createStyleContext } from '~/lib/create-style-context'

const { withProvider, withContext } = createStyleContext(slider)

export type RootProviderProps = Component<typeof RootProvider>
export const RootProvider = withProvider<
  Assign<HTMLStyledProps<'div'>, Slider.RootProviderBaseProps>
>(Slider.RootProvider, 'root')

export type RootProps = Component<typeof Root>
export const Root = withProvider<Assign<HTMLStyledProps<'div'>, Slider.RootBaseProps>>(
  Slider.Root,
  'root',
)

export type ControlProps = Component<typeof Control>
export const Control = withContext<Assign<HTMLStyledProps<'div'>, Slider.ControlBaseProps>>(
  Slider.Control,
  'control',
)

export type LabelProps = Component<typeof Label>
export const Label = withContext<Assign<HTMLStyledProps<'label'>, Slider.LabelBaseProps>>(
  Slider.Label,
  'label',
)

export type MarkerGroupProps = Component<typeof MarkerGroup>
export const MarkerGroup = withContext<Assign<HTMLStyledProps<'div'>, Slider.MarkerGroupBaseProps>>(
  Slider.MarkerGroup,
  'markerGroup',
)

export type MarkerProps = Component<typeof Marker>
export const Marker = withContext<Assign<HTMLStyledProps<'span'>, Slider.MarkerBaseProps>>(
  Slider.Marker,
  'marker',
)

export type RangeProps = Component<typeof Range>
export const Range = withContext<Assign<HTMLStyledProps<'div'>, Slider.RangeBaseProps>>(
  Slider.Range,
  'range',
)

export type ThumbProps = Component<typeof Thumb>
export const Thumb = withContext<Assign<HTMLStyledProps<'div'>, Slider.ThumbBaseProps>>(
  Slider.Thumb,
  'thumb',
)

export type TrackProps = Component<typeof Track>
export const Track = withContext<Assign<HTMLStyledProps<'div'>, Slider.TrackBaseProps>>(
  Slider.Track,
  'track',
)

export type ValueTextProps = Component<typeof ValueText>
export const ValueText = withContext<Assign<HTMLStyledProps<'span'>, Slider.ValueTextBaseProps>>(
  Slider.ValueText,
  'valueText',
)

export {
  SliderContext as Context,
  type SliderContextProps as ContextProps,
  SliderHiddenInput as HiddenInput,
  type SliderHiddenInputProps as HiddenInputProps,
} from '@ark-ui/vue/slider'
